import React from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import List from "./List";

export default function Lists({ budgetData, setBudgetData }) {

  const handleEnd = (result) => {
    if (!result.destination) return;

    const newBudgetData = Array.from(budgetData);
    const [reorderedItem] = newBudgetData.splice(result.source.index, 1);
    newBudgetData.splice(result.destination.index, 0, reorderedItem);

    setBudgetData(newBudgetData);
    localStorage.setItem("budgetData", JSON.stringify(newBudgetData));
  };

  return (
    <div>
      <DragDropContext onDragEnd={handleEnd}>
        <Droppable droppableId="budget-items">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {budgetData.map((data, index) => (
                <Draggable
                  key={data.id}
                  draggableId={data.id.toString()}
                  index={index}
                >
                  {(provided, snapshot) => (
                    <List
                      key={data.id}
                      id={data.id}
                      item={data.item}
                      cost={data.cost}
                      budgetData={budgetData}
                      setBudgetData={setBudgetData}
                      provided={provided}
                      snapshot={snapshot}
                    />
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}
