import React from 'react'

export default function Form({ handleSubmit, item, setItem, cost, setCost }) {

    const handleItemChange = (e) => {
        setItem(e.target.value);
    };

    const handleCostChange = (e) => {
        setCost(e.target.value);
    };

    return (
        <form onSubmit={handleSubmit} className="flex">
            <div className="p-4 flex-1">
                <label htmlFor="item">지출 항목</label>
                <input 
                    type="text"
                    name="item"
                    className="bg-transparent border-none outline-none h-12 w-full font-sans mx-0 my-0 p-0"
                    placeholder="예) 렌트비"
                    value={item}
                    onChange={handleItemChange}
                />
            </div>
            <div className="p-4 flex-1">
                <label htmlFor="cost">비용</label>
                <input 
                    type="number"
                    name="cost"
                    className="bg-transparent border-none outline-none h-12 w-full font-sans mx-0 my-0 p-0"
                    placeholder="예) 100"
                    value={cost}
                    onChange={handleCostChange}
                />
            </div>
            <input 
                type="submit"
                value="제출"
                className="p-2 text-blue-400 border-2 border-blue-400 rounded hover:text-white hover:bg-blue-200"
            />
        </form>
    )
}
