import React, {useState} from 'react'

export default function Lists({ todoData, setTodoData }) {

      const [isEditing, setIsEditing] = useState(false);
      const [editedTitle, setEditedTitle] = useState(todoData.title);

      const handleClick = (id) => {
        let newTodoData = todoData.filter((data) => data.id !== id);
        console.log('newTodoData', newTodoData);
        setTodoData(newTodoData);
      };

      const handleCompleChange = (id) => {
        let newTodoData = todoData.map((data) => {
          if (data.id === id) {
            data.completed = !data.completed;
          }
          return data;
        });
        setTodoData(newTodoData);
      };

      const handleEditChange = (e) => {
        setEditedTitle(e.target.value);
      };

  if(isEditing) {
    return(
      <div>
          {todoData.map((data) => (
              <div key={data.id}>
                <div className={`flex items-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded`}>
                  <div className="items-center">
                  <form onSubmit={handleSubmit}>
                    <input 
                    className="w-full px-3 py-2 mr-4 text-gray-500" 
                    value={editedTitle} 
                    onChange={handleEditChange}
                    autoFocus 
                    />
                  </form>
                  </div>
                  <div className="items-center">
                  <button className="px-4 py-2 float-right" onClick={() => setIsEditing(false)}>x</button>
                  <button className="px-4 py-2 float-right" type="submit" onClick={handleSubmit}>save</button>
                  </div>
                </div>
              </div>
            ))}
      </div>
    )
  } else {
    return (
      <div>
          {todoData.map((data) => (
              <div key={data.id}>
                <div className={`flex items-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded`}>
                  <div className="items-center">
                  <input type="checkbox" onChange={() => handleCompleChange(data.id)} defaultChecked={false} />
                  <span className={data.completed && "line-through"}>{data.title}</span>
                  </div>
                  <div className="items-center">
                  <button className="px-4 py-2 float-right" onClick={() => handleClick(data.id)}>x</button>
                  <button className="px-4 py-2 float-right" onClick={() => setIsEditing(true)}>edit</button>
                  </div>
                </div>
              </div>
            ))}
      </div>
    )
  }
}
