import React, { useState } from 'react';

const List = ({ id, item, cost, budgetData, setBudgetData, provided, snapshot }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editedItem, setEditedItem] = useState(item);
    const [editedCost, setEditedCost] = useState(cost.toString());

    const handleRemove = (id) => {
        const newBudgetData = budgetData.filter(data => data.id !== id);
        setBudgetData(newBudgetData);
        localStorage.setItem("budgetData", JSON.stringify(newBudgetData));
    };

    const handleEditSubmit = (e) => {
        e.preventDefault();
        const newBudgetData = budgetData.map(data => {
            if (data.id === id) {
                return { ...data, item: editedItem, cost: parseInt(editedCost, 10) };
            }
            return data;
        });
        setBudgetData(newBudgetData);
        localStorage.setItem("budgetData", JSON.stringify(newBudgetData));
        setIsEditing(false);
    };

    return (
        <div
            {...provided.draggableProps}
            ref={provided.innerRef}
            {...provided.dragHandleProps}
            className={`${snapshot.isDragging ? "bg-gray-400" : "bg-gray-100"} flex justify-between items-center w-full p-4 my-2 rounded border`}
        >
            {isEditing ? (
                <form onSubmit={handleEditSubmit} className="w-full flex justify-between">
                    <input
                        type="text"
                        value={editedItem}
                        onChange={(e) => setEditedItem(e.target.value)}
                        className="text-gray-600 p-2 w-full rounded mr-2"
                    />
                    <input
                        type="number"
                        value={editedCost}
                        onChange={(e) => setEditedCost(e.target.value)}
                        className="text-gray-600 p-2 w-full rounded mr-2"
                    />
                    <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded">Save</button>
                </form>
            ) : (
                <React.Fragment>
                    <span>{item} - {cost}원</span>
                    <div>
                        <button onClick={() => setIsEditing(true)} className="px-4 py-2 mr-2 bg-gray-200 rounded">Edit</button>
                        <button onClick={() => handleRemove(id)} className="px-4 py-2 bg-red-500 text-white rounded">Remove</button>
                    </div>
                </React.Fragment>
            )}
        </div>
    );
};

export default List;
